<svelte:head>
    <script src="https://checkout.razorpay.com/v1/checkout.js"></script>
</svelte:head>
<script>
    import { Card, Button, GradientButton, Input, ButtonGroup, Toast, CloseButton, Badge, Modal, Spinner } from "flowbite-svelte";
    import { fly } from 'svelte/transition';
    import { enhance } from '$app/forms';
    import { invalidateAll } from '$app/navigation';

    export let data;

    let selectedToken = null;
    let tokenModal = false;
    let tokenDeleteModal = false;

    let loading = false;
    let loadingTokenDelete = false;

    function obfuscateString(str) {
        return '*'.repeat(str.length - 4) + str.slice(-4);
    }

    function revokeToken(token) {
        selectedToken = token;
        tokenDeleteModal = true;
    }

    function copyToken(e, token) {
        e.target.textContent = 'Copied';

        const textarea = document.createElement('textarea');
        textarea.value = token;
        document.body.appendChild(textarea);
        
        textarea.select();
        document.execCommand('copy');

        document.body.removeChild(textarea);

        setTimeout(() => {
            e.target.textContent = 'Copy';
        }, 500);
    }

    async function deleteToken() {
        loadingTokenDelete = true;
        let response = await fetch('/api/delete-token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ tokenId: selectedToken.id }),
        });
        const result = await response.json();
        loadingTokenDelete = false;
        tokenDeleteModal = false;
        toastMessage = 'Token deleted successfully';
        triggerToast();
        invalidateAll();
    }

    // Toast Function
    let toast = false;
    let toastMessage = null;
    let counter = 5;
    let toastTimeout;
    function triggerToast() {
        if (toast) {
            closeToast();
        }
        toast = true;
        counter = 5;
        timeout();
    }

    function closeToast() {
        toast = false;
        clearTimeout(toastTimeout);
    }

    function timeout() {
        if (--counter > 0) {
            toastTimeout = setTimeout(timeout, 1000);
        } else {
            closeToast();
        }
    }
</script>
<main class="p-4 md:ml-60 min-h-screen">
    <div class="bg-white dark:bg-gray-800 relative shadow-md sm:rounded-lg overflow-hidden">
        <div class="flex flex-col md:flex-row items-center justify-between space-y-3 md:space-y-0 md:space-x-4 p-4 border-b border-gray-200 dark:border-gray-700">
            <div class="w-full md:w-1/2">
                <h3 class="text-xl font-semibold dark:text-white">Access tokens</h3>
            </div>
            <div>
                <Button on:click={()=>tokenModal=true}>Generate Token</Button>
            </div>
        </div>
        {#if data.tokens.length > 0}
        <div class="overflow-x-auto">
            <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400">
                <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                    <tr>
                        <th scope="col" class="px-4 py-3 w-1/6">Name</th>
                        <th scope="col" class="px-4 py-3 w-1/3">Token</th>
                        <th scope="col" class="px-4 py-3 w-1/6">Created</th>
                        <th scope="col" class="px-4 py-3 w-1/6">
                            <span class="sr-only">Action</span>
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {#each data.tokens as token}
                        <tr class="border-b dark:border-gray-700">
                            <td class="px-4 py-3">{token.name}</td>
                            <td class="px-4 py-3">
                                <div class="relative">
                                    <input value={obfuscateString(token.token)} class="block w-full p-2.5 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" disabled>
                                    <button on:click={(e)=>copyToken(e, token.token)} class="text-xs text-white absolute end-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-1 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Copy</button>
                                </div>
                            </td>
                            <td class="px-4 py-3">{token.timestamp.toLocaleString("en-IN", {"dateStyle": "long", "timeStyle": "short"})}</td>
                            <td class="px-4 py-3 flex items-center justify-end">
                                <Button outline color="red" size="sm" on:click={()=>revokeToken(token)}>Revoke</Button>
                            </td>
                        </tr>
                    {/each}
                </tbody>
            </table>
        </div>
        {/if}
    </div>

    <div class="mt-5 bg-white dark:bg-gray-800 relative shadow-md sm:rounded-lg">
        <div class="grid grid-cols-3 gap-4 p-4">
            <div class="p-6 bg-white border border-gray-200 rounded-lg dark:bg-gray-800 dark:border-gray-700">
                <img class="mb-4 h-[50px] rounded-lg" src='data:image/png;base64,' alt='API Logo'>
                <h5 class="mb-2 text-xl font-bold tracking-tight text-gray-900 dark:text-white">REST API</h5>
                <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Build custom integrations with NetCredential via REST API</p>
                <a href="https://documenter.getpostman.com/view/19678292/2s9YsFFEZN" target="_blank" class="font-medium text-blue-600 dark:text-blue-500 hover:underline underline">View documentation</a>
            </div>
            <div class="p-6 bg-white border border-gray-200 rounded-lg dark:bg-gray-800 dark:border-gray-700 relative">
                <div class="absolute top-0 right-0 bg-red-600 text-white p-1 text-sm font-bold rounded-bl-lg">
                    Coming Soon
                </div>
                <img class="mb-4 h-[50px] rounded-lg" src='data:image/png;base64,' alt='Zapier Logo'>
                <h5 class="mb-2 text-xl font-bold tracking-tight text-gray-900 dark:text-white">Zapier</h5>
                <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Create custom automations and connect NetCredential to 5,000+ apps.</p>
                <!-- <a class="font-medium text-blue-600 dark:text-blue-500 hover:underline underline">View integration</a> -->
            </div>
        </div>
    </div>
</main>

<Modal class="text-center" bind:open={tokenDeleteModal} size='xs'>
    <div class="d-block">
        <h3 class="mb-2 text-gray-500 dark:text-gray-300">Are you sure you want to revoke this token?</h3>
        <p class="mb-4">Once you revoke <strong>{selectedToken.name}</strong> token, you can no longer use it to access NetCredential data.</p>
        <div class="flex justify-center items-center space-x-4">
            <button type="submit" on:click={deleteToken} class="py-2 px-3 text-sm font-medium text-center text-white bg-red-600 rounded-lg hover:bg-red-700 focus:ring-4 focus:outline-none focus:ring-red-300 dark:bg-red-500 dark:hover:bg-red-600 dark:focus:ring-red-900">
                {#if loadingTokenDelete}
                    <Spinner size="5" color="white" />
                {:else}
                    Yes, I'm sure
                {/if}
            </button>
            <button on:click={()=>tokenDeleteModal=false} type="button" class="py-2 px-3 text-sm font-medium text-gray-500 bg-white rounded-lg border border-gray-200 hover:bg-gray-100 focus:ring-4 focus:outline-none focus:ring-primary-300 hover:text-gray-900 focus:z-10 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-500 dark:hover:text-white dark:hover:bg-gray-600 dark:focus:ring-gray-600">
                No, cancel
            </button>
        </div>
    </div>
</Modal>

<Modal title="Generate Access Token" bind:open={tokenModal}>
    <form class="space-y-4 md:space-y-6" method="POST"
                use:enhance={() => {
                    loading = true;
                    return async ({ update }) => {
                        await update({ reset: false });
                        loading = false;
                        tokenModal = false;
                        toastMessage = 'Token created successfully';
                        triggerToast();
                        invalidateAll();
                    };
                }}>
    <div>
        <label
            for="access-token-name"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
            >Access token name</label
        >
        <input
            name="token"
            type="text"
            id="access-token-name"
            class="bg-white border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            required
        />
        <p class="mt-2 text-sm text-gray-500 dark:text-gray-400">Enter a unique name for your token to differentiate it from the other tokens.</p>
    </div>
    <Button type="submit">
        {#if loading}
            <Spinner size="5" color="white" />
        {:else}
            Generate
        {/if}
    </Button>
    </form>
</Modal>

<Toast transition={fly} params="{{x: 200}}" color="green" position="top-right" open={toast} class="fixed">
    <svelte:fragment slot="icon">
      <i class="icon-check"></i>
    </svelte:fragment>
    {toastMessage}
</Toast>